import React, { useEffect, useState } from 'react'
import { db } from '../firebase.js'

function Line() {

    //メッセージ情報
    const [messages, setMessages] = useState([]);

    //setMessageの情報を１つずつ取得する
    useEffect(() => {
        db.collection("messages")
        .orderBy("createdAt")
        .limit(50)
        .onSnapshot((snapshot) => {
            setMessages(snapshot.docs.map((doc) => doc.data()));
        });
    }, []);


